mapper.select:
select seq, title,date_format(T_dateTime,'%Y-%m-%d') as T_dateTime,date_format(T_endTime,'%Y-%m-%d') as T_endTime,state from todo
 where 

 T_dateTime <= #{day} 
 and T_endTime >= #{day} and 

 e_number=#{e_number}

 and state != '対応済'

select seq,title,content,referenceFile,date_format(T_dateTime,'%Y-%m-%d') as T_dateTime,date_format(T_endTime,'%Y-%m-%d') as T_endTime,
date_format(T_dateTime,'%H:%i') as startTime,date_format(T_endTime,'%H:%i') as endTime,e_number,state
from todo where seq=#{seq}
